sys_script_include.script:
function validateEmailAddress(emailStr){
		// Use JavaScript coercion to guarantee emailStr is a string
		emailStr = emailStr + '';

		//queries emails of users
		var gr = new GlideRecord('sys_user');
		gr.addEncodedQuery("emailLIKE@");
		gr.query();

		// Compare emailStr to user email query
		while(gr.next()){
			if(emailStr === gr){
				return true;
			}
		}
		return false;
	}
